def fibonacci(input):
    a= datetime.now()
    global n3
    n1 = len(input)
    n2 = len(input)* math.pi
    for i in range(len(input)):
        n3 = int(n1+n2)
        n2 = int(n1)
        n1 = int(n3)
        n3 = n3* a.second
        n3 = n3//n2
    return n3

